import React, { Component } from 'react';

let base = (child)=> class extends Component {
    constructor(){
        super();
        this.state = {
            id: 100
        };
    }
    UpdateCount(){
        this.setState({
            id:1000
        })

    }
    render(){
        return(
            <div>
            <h2>Child-1</h2>
            <child id={this.state.id} UpdateCount={() => this.UpdateCount()}/>
            </div>

        )
    }

}
const Button = (props)=>{
    return(
    <button onClick="(props.UpdateCount)"></button>)
}
let NewButton = base(Button);
class HOCProps extends Component {
    render(){
        return(
            <div>
                This is Parent
                <NewButton/>
            </div>
        )
    }

}

export default HOCProps;
