}

const Button = () => {
    return(
        <h4>This is Button</h4>
    );
}
let NewButton = BaseComponent(Button);

class HOC extends Component{
    render(){
        return(
            <div>
                Hello
                <NewButton/>
            </div>
        );
    }
}

