import React, { Component } from 'react';

class Forms extends Component {
    constructor(){
        super();
        this.state={
            username : '',
            password : ''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleChange1= this.handleChange1.bind(this);
    }
    handleChange(event){
        this.setState({
            username:event.target.value
        })
    }
    handleChange1(event){
        this.setState({
            password:event.target.value
        })
    }
    render(){
        return(
            <div>
                <h3> ControlledForms in React </h3>
                User: <input type="text" placeholder="username" value={this.state.username} onChange={()=>this.handleChange}/>
                Password: <input type="password" placeholder="password"  value={this.state.password} onChange={()=>this.handleChange1}/>
                <input type="submit" value="Submit" />
            </div>
        )
    }
}

export default Forms;
